def number
puts "Combien y a-t-il de journalistes dans cet array ?"
puts "> #{array_handle.size+1}"
puts " \n"
end

def num_handle
puts "Combien d'handle contiennent un numéro ?"
puts (array_handle.select{|item| item.include?("1")} + array_handle.select{|item| item.include?("2")} + array_handle.select{|item| item.include?("3")} + array_handle.select{|item| item.include?("4")} + array_handle.select{|item| item.include?("5")} + array_handle.select{|item| item.include?("6")} + array_handle.select{|item| item.include?("7")} + array_handle.select{|item| item.include?("8")} + array_handle.select{|item| item.include?("9")}).uniq.size
puts " \n"
end

def four_letters
puts "Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ?"
puts "> Il y en a #{}."
puts " \n"
end

def start_maj
puts "Combien commencent par une majuscule (première lettre juste après le @) ?"
puts "> Il y en a #{}."
puts " \n"
end

def maj_include
puts "Combien contiennent au moins une majuscule ?"
puts (array_handle.select{|item| item.include?("A")} + array_handle.select{|item| item.include?("B")} + array_handle.select{|item| item.include?("C")} + array_handle.select{|item| item.include?("D")} + array_handle.select{|item| item.include?("E")} + array_handle.select{|item| item.include?("F")} + array_handle.select{|item| item.include?("G")} + array_handle.select{|item| item.include?("H")} + array_handle.select{|item| item.include?("I")} + array_handle.select{|item| item.include?("J")} + array_handle.select{|item| item.include?("K")} + array_handle.select{|item| item.include?("L")} + array_handle.select{|item| item.include?("M")} + array_handle.select{|item| item.include?("N")} + array_handle.select{|item| item.include?("O")} + array_handle.select{|item| item.include?("P")} + array_handle.select{|item| item.include?("Q")} + array_handle.select{|item| item.include?("R")} + array_handle.select{|item| item.include?("S")} + array_handle.select{|item| item.include?("T")} + array_handle.select{|item| item.include?("U")} + array_handle.select{|item| item.include?("V")} + array_handle.select{|item| item.include?("W")} + array_handle.select{|item| item.include?("X")} + array_handle.select{|item| item.include?("Y")} + array_handle.select{|item| item.include?("Z")}).uniq.size
puts " \n"
end

def underscore
puts "Combien y a-t-il de underscore _ dans tous les pseudos confondus ?"
puts "> Il y en a #{array_handle.select{|item| item.include?("_")}.size}."
puts " \n"
end
# la 1e partie {array_handle.select{|item| item.include?("_")} fait apparaître tous les handles contenant un _.
# la méthode .size calcule le nombre de handles incluant un _.

def alphabetical_order
puts "Trie la liste de handle par ordre alphabétique."
print "> #{array_handle.sort}"
puts " \n"
end

def shorter
puts "Quels sont les 50 handles les plus courts de ce array ?"
print"> #{array_handle.min(50){|a,b| a.length <=> b.length}}"
puts " \n"
end

def position
puts "Quelle est la position dans l'array de la personne @epenser ?"
puts "> #{array_handle.index("@epenser")}"
puts " \n"
end

def perform
	number
	num_handle
	four_letters
	start_maj
	maj_include
	underscore
	alphabetical_order
	shorter
	position
end

perform
